import { useParams } from 'react-router-dom'

function BookDetails() {
    const id=useParams().id;
    console.log(id);
    useEffect(() => {
        const fetchHandler = async () => {
          await axios
            .get(`http://localhost:5000/books/${id}`)
            .then((res) => res.data)
            .then((data) => console.log(id));
        };
        fetchHandler();
      }, [id]);
    
  return (
    <div></div>
  )
}

